page.script:
itemCounter = 0;
function OnAddItem()
local listBox = GUIContext:GetElement("lb0");
if listBox then
listBox:AddItem(string.format("Item %d", itemCounter));
itemCounter = itemCounter + 1;
end;
end;

function OnRemoveItem()
local listBox = GUIContext:GetElement("lb0");
if listBox then
listBox:RemoveItem(0);
end;
end;

function OnRemoveAll()
local listBox = GUIContext:GetElement("lb0");
if listBox then
listBox:Clear();
end;
end;
